import React, { useState } from "react";
import { Dialog } from "@headlessui/react";

const data = {
  "Week 1": [
    {
      title: "React Basics",
      type: "video",
      url: "https://www.youtube.com/embed/dGcsHMXbSOA",
    },
    {
      title: "React Docs",
      type: "article",
      url: "https://reactjs.org/docs/getting-started.html",
    },
  ],
  "Week 2": [
    {
      title: "React useState Hook",
      type: "video",
      url: "https://www.youtube.com/embed/O6P86uwfdR0",
    },
    {
      title: "State & Lifecycle",
      type: "article",
      url: "https://reactjs.org/docs/state-and-lifecycle.html",
    },
  ],
};

export default function Modules() {
  const [open, setOpen] = useState(false);
  const [videoUrl, setVideoUrl] = useState("");
  const [darkMode, setDarkMode] = useState(false);

  const handleClick = (resource) => {
    if (resource.type === "video") {
      setVideoUrl(resource.url);
      setOpen(true);
    } else {
      window.open(resource.url, "_blank");
    }
  };

  return (
    <div
      className={`${
        darkMode ? "dark" : ""
      } min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors`}
    >
      <div className="max-w-5xl mx-auto px-4 py-8">
        {/* Header + Dark mode toggle */}
        <header className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-gray-100">
            Learning Modules
          </h1>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200 px-4 py-2 rounded-md hover:bg-gray-300 dark:hover:bg-gray-600 transition"
            aria-label="Toggle Dark Mode"
          >
            {darkMode ? "Light Mode" : "Dark Mode"}
          </button>
        </header>

        {/* Weeks and resources */}
        {Object.entries(data).map(([week, resources]) => (
          <section key={week} className="mb-12">
            <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-gray-200">
              {week}
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
              {resources.map((res, idx) => (
                <div
                  key={idx}
                  onClick={() => handleClick(res)}
                  className="cursor-pointer rounded-lg bg-blue-100 dark:bg-blue-900 p-5 shadow hover:shadow-lg hover:bg-blue-200 dark:hover:bg-blue-800 transition"
                  role="button"
                  tabIndex={0}
                  onKeyPress={(e) => {
                    if (e.key === "Enter") handleClick(res);
                  }}
                >
                  <h3 className="font-semibold text-gray-900 dark:text-gray-100">
                    {res.title}
                  </h3>
                  <p className="mt-1 text-sm text-gray-700 dark:text-gray-300 capitalize">
                    {res.type}
                  </p>
                </div>
              ))}
            </div>
          </section>
        ))}

        {/* Video Modal */}
        <Dialog
          open={open}
          onClose={() => setOpen(false)}
          className="fixed z-20 inset-0 overflow-y-auto"
        >
          <div className="flex items-center justify-center min-h-screen px-4 bg-black bg-opacity-70">
            <Dialog.Panel className="bg-white dark:bg-gray-800 rounded-lg max-w-3xl w-full p-4 shadow-lg">
              <iframe
                width="100%"
                height="360"
                src={videoUrl}
                title="YouTube video"
                frameBorder="0"
                allowFullScreen
                className="rounded-md"
              ></iframe>
              <button
                onClick={() => setOpen(false)}
                className="mt-4 px-4 py-2 bg-red-500 hover:bg-red-600 text-white rounded-md transition"
              >
                Close
              </button>
            </Dialog.Panel>
          </div>
        </Dialog>
      </div>
    </div>
  );
}
