import React, { useState } from "react";
import { Dialog } from "@headlessui/react";

const data = {
  "Week 1": [
    {
      title: "React Basics",
      type: "video",
      url: "https://www.youtube.com/embed/dGcsHMXbSOA",
    },
    {
      title: "React Docs",
      type: "article",
      url: "https://reactjs.org/docs/getting-started.html",
    },
  ],
  "Week 2": [
    {
      title: "React useState Hook",
      type: "video",
      url: "https://www.youtube.com/embed/O6P86uwfdR0",
    },
    {
      title: "State & Lifecycle",
      type: "article",
      url: "https://reactjs.org/docs/state-and-lifecycle.html",
    },
  ],
};

export default function Modules() {
  const [open, setOpen] = useState(false);
  const [videoUrl, setVideoUrl] = useState("");

  const handleClick = (resource) => {
    if (resource.type === "video") {
      setVideoUrl(resource.url);
      setOpen(true);
    } else {
      window.open(resource.url, "_blank");
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1
        style={{ fontSize: "2rem", textAlign: "center", marginBottom: "1rem" }}
      >
        Learning Modules
      </h1>

      {Object.entries(data).map(([week, resources]) => (
        <div key={week} style={{ marginBottom: "2rem" }}>
          <h2>{week}</h2>
          <div style={{ display: "flex", gap: "1rem", flexWrap: "wrap" }}>
            {resources.map((res, index) => (
              <div
                key={index}
                onClick={() => handleClick(res)}
                style={{
                  padding: "1rem",
                  borderRadius: "8px",
                  background: "#e0f2fe",
                  cursor: "pointer",
                  minWidth: "160px",
                }}
              >
                {res.title}
              </div>
            ))}
          </div>
        </div>
      ))}

      <Dialog
        open={open}
        onClose={() => setOpen(false)}
        className="fixed z-10 inset-0 overflow-y-auto"
      >
        <div className="flex items-center justify-center min-h-screen bg-black bg-opacity-50">
          <Dialog.Panel className="bg-white p-6 rounded">
            <iframe
              width="560"
              height="315"
              src={videoUrl}
              title="YouTube video"
              frameBorder="0"
              allowFullScreen
            ></iframe>
            <button
              onClick={() => setOpen(false)}
              style={{ marginTop: "1rem" }}
            >
              Close
            </button>
          </Dialog.Panel>
        </div>
      </Dialog>
    </div>
  );
}
